import React, { Component } from 'react';
import axios from 'axios';
import { Card, Table, Button, ButtonGroup } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faList, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import MyToast from './myToast';
import { Link } from 'react-router-dom';

const styles = `
  .voiture-liste .my-toast {
    position: absolute;
    top: 10px;
    right: 10px;
  }

  .voiture-liste .card {
    border: none;
    background-color: #343a40;
    color: white;
    margin-bottom: 20px;
  }

  .voiture-liste .card-header {
    background-color: #343a40;
    border-bottom: 1px solid #495057;
    color: white;
    font-size: 24px;
  }

  .voiture-liste .table {
    border-collapse: collapse;
    width: 100%;
  }

  .voiture-liste .table th {
    background-color: #495057;
    color: white;
  }

  .voiture-liste .table td {
    color: white;
  }

  .voiture-liste .table-hover tbody tr:hover {
    background-color: #5a6268;
  }

  .voiture-liste .btn-group .btn {
    margin: 0 5px;
  }

  .voiture-liste .card-footer {
    background-color: #343a40;
    border-top: 1px solid #495057;
    color: white;
  }
`;

class VoitureListe extends Component {
    constructor(props) {
        super(props);
        this.state = {
            voitures: [],
            show: false,
            message: "",
            voitureIdToDelete: null,
        };
    }

    componentDidMount() {
        this.fetchVoitures();
    }

    fetchVoitures() {
        axios.get('http://localhost:8080/voitures')
            .then((response) => response.data)
            .then((data) => {
                this.setState({ voitures: data });
            })
            .catch((error) => {
                console.error('Erreur lors de la récupération des données :', error);
            });
    }

    handleEditClick(voitureId) {
        console.log("Édition de la voiture avec l'ID : " + voitureId);
    }

    handleDeleteClick(voitureId) {
        this.setState({ show: true, message: "Voiture supprimée avec succès.", voitureIdToDelete: voitureId });

        setTimeout(() => {
            this.setState({ show: false, message: "", voitureIdToDelete: null });
        }, 3000);

        axios.delete(`http://localhost:8080/voitures/${voitureId}`)
            .then((response) => {
                if (response.data !== null) {
                    this.fetchVoitures();
                }
            })
            .catch((error) => {
                console.error('Erreur lors de la suppression de la voiture :', error);
            });
    }

    render() {
        return (
            <div className="voiture-liste">
                <style>{styles}</style>
                <div style={{ display: this.state.show ? "block" : "none" }}>
                    <MyToast show={this.state.show} message="Voiture supprimée avec succès" type="danger" />
                </div>

                <Card className="border border-dark bg-dark text-white">
                    <Card.Header><FontAwesomeIcon icon={faList} /> Liste Voitures</Card.Header>
                    <Card.Body>
                        <Table bordered hover striped variant="dark">
                            <thead>
                            <tr>
                                <th>Marque</th>
                                <th>Modèle</th>
                                <th>Couleur</th>
                                <th>Immatricule</th>
                                <th>Année</th>
                                <th>Prix</th>
                                <th>Actions</th>
                            </tr>
                            </thead>
                            <tbody>
                            {this.state.voitures.length ? (
                                this.state.voitures.map((voiture) => (
                                    <tr key={voiture.id}>
                                        <td>{voiture.marque}</td>
                                        <td>{voiture.modele}</td>
                                        <td>{voiture.couleur}</td>
                                        <td>{voiture.immatricule}</td>
                                        <td>{voiture.annee}</td>
                                        <td>{voiture.prix}</td>
                                        <td>
                                            <ButtonGroup>
                                                <Link to={'/edit/' + voiture.id} className="btn btn-sm btn-outline-primary" style={{ marginRight: '8px' }}>
                                                    <FontAwesomeIcon icon={faEdit} />
                                                </Link>
                                                <Button
                                                    variant="outline-danger"
                                                    onClick={() => this.handleDeleteClick(voiture.id)}
                                                    style={{ marginLeft: '8px' }}
                                                >
                                                    <FontAwesomeIcon icon={faTrash} />
                                                </Button>
                                            </ButtonGroup>
                                        </td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="7" align="center">
                                        Aucune Voiture n'est disponible.
                                    </td>
                                </tr>
                            )}
                            </tbody>
                        </Table>
                    </Card.Body>
                    <Card.Footer>
                        <Table bordered hover striped variant="dark">
                            <tbody>
                            <tr>
                                <td colSpan="7" align="center">
                                    {this.state.voitures.length} Voitures disponibles
                                </td>
                            </tr>
                            </tbody>
                        </Table>
                    </Card.Footer>
                </Card>
            </div>
        );
    }
}

export default VoitureListe;
