import React from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';

class Bienvenue extends React.Component {
    render() {
        const bienvenueStyle = {
            marginTop: '20px',
            textAlign: 'center'
        };

        const cardStyle = {
            backgroundColor: '#343a40', // Couleur de fond
            color: 'white', // Couleur du texte
            padding: '20px',
            border: '1px solid #6c757d', // Bordure
            borderRadius: '5px', // Coins arrondis
        };

        const titleStyle = {
            fontSize: '28px',
            marginBottom: '20px'
        };

        const blockquoteStyle = {
            fontSize: '18px'
        };

        const footerStyle = {
            fontSize: '14px'
        };

        return (
            <Container style={bienvenueStyle}>
                <Row>
                    <Col lg={12}>
                        <Card style={cardStyle}>
                            <h1 style={titleStyle}>Bienvenue au Magasin des Voitures</h1>
                            <blockquote style={blockquoteStyle}>
                                <p>Le meilleur de nos voitures est exposé près de chez vous</p>
                                <footer style={footerStyle}>IDSIT</footer>
                            </blockquote>
                        </Card>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default Bienvenue;
