import React, { Component } from 'react';
import axios from 'axios';
import { Button, Card, Form, FormGroup, Col, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlusSquare, faSave, faUndo } from "@fortawesome/free-solid-svg-icons";
import MyToast from './myToast';

class Voiture extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState;
        this.state.isEdit = false; // Ajout de l'état pour le mode édition

        // Lier la méthode à l'instance de la classe dans le constructeur
        this.voitureChange = this.voitureChange.bind(this);
        this.submitVoiture = this.submitVoiture.bind(this);
        this.resetVoiture = this.resetVoiture.bind(this);
        this.toggleToast = this.toggleToast.bind(this);
    }

    initialState = {
        marque: '',
        modele: '',
        couleur: '',
        immatricule: '',
        annee: '',
        prix: '',
        showToast: false, // État pour gérer l'affichage du toast
    };

    componentDidMount() {
        const { id } = this.props.match.params;
        if (id) {
            // Le composant est en mode modification
            this.setState({ isEdit: true });
            // Chargez les données de la voiture à modifier en fonction de l'ID
            // Utilisez une requête GET pour obtenir les données de la voiture
            axios.get(`http://localhost:8080/voitures/${id}`)
                .then(response => {
                    this.setState({
                        ...response.data,
                        isEdit: true,
                    });
                })
                .catch(error => {
                    console.error("Erreur lors du chargement des données de la voiture :", error);
                });
        }
    }

    resetVoiture() {
        this.setState(this.initialState);
    }

    submitVoiture(event) {
        event.preventDefault();
        const { isEdit, ...voitureData } = this.state;

        if (isEdit) {
            // En mode modification, envoyez une requête PUT pour mettre à jour la voiture
            axios.put(`http://localhost:8080/voitures/${this.props.match.params.id}`, voitureData)
                .then(response => {
                    if (response.data) {
                        this.toggleToast(true);
                    }
                })
                .catch(error => {
                    console.error("Erreur lors de la mise à jour de la voiture :", error);
                });
        } else {
            // En mode ajout, envoyez une requête POST pour ajouter la voiture
            axios.post("http://localhost:8080/voitures", voitureData)
                .then(response => {
                    if (response.data) {
                        this.toggleToast(true);
                    }
                })
                .catch(error => {
                    console.error("Erreur lors de l'ajout de la voiture :", error);
                });
        }
    }

    toggleToast(show) {
        this.setState({ showToast: show });
    }

    voitureChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
        });
    }

    render() {
        return (
            <div>
                <div style={{ display: this.state.showToast ? "block" : "none" }}>
                    <MyToast show={this.state.showToast} message="Voiture enregistrée avec succès" type="success" />
                </div>
                <Card className="border border-dark bg-dark text-white">
                    <Card.Header><FontAwesomeIcon icon={faPlusSquare} /> {this.state.isEdit ? "Modifier" : "Ajouter"} Voiture</Card.Header>
                    <Card.Body>
                        <Form onReset={this.resetVoiture} onSubmit={this.submitVoiture} id="VoitureFormId">
                            <Row>
                                <Col>
                                    <FormGroup controlId="formGridMarque">
                                        <Form.Label>Marque</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Marque Voiture"
                                            name="marque"
                                            value={this.state.marque}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridModele">
                                        <Form.Label>Modèle</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Modèle Voiture"
                                            name="modele"
                                            value={this.state.modele}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridCouleur">
                                        <Form.Label>Couleur</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Couleur Voiture"
                                            name="couleur"
                                            value={this.state.couleur}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridImmatricule">
                                        <Form.Label>Immatricule</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Immatricule Voiture"
                                            name="immatricule"
                                            value={this.state.immatricule}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridAnnee">
                                        <Form.Label>Année</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Année Voiture"
                                            name="annee"
                                            value={this.state.annee}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridPrix">
                                        <Form.Label>Prix</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Prix Voiture"
                                            name="prix"
                                            value={this.state.prix}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                            </Row>
                            <Button size="sm" variant="success" type="submit"><FontAwesomeIcon icon={faSave} /> {this.state.isEdit ? "Enregistrer les modifications" : "Ajouter la voiture"}</Button>{' '}
                            <Button size="sm" variant="info" type="reset"><FontAwesomeIcon icon={faUndo} /> Reset</Button>
                        </Form>
                    </Card.Body>
                </Card>
            </div>
        );
    }
}

export default Voiture;
