import React, { Component } from 'react';
import axios from 'axios';
import { Button, Card, Form, FormGroup, Col, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlusSquare, faSave, faUndo } from "@fortawesome/free-solid-svg-icons";
import MyToast from './myToast';

class Voiture extends Component {
    constructor(props) {
        super(props);
        this.state = this.initialState;

        // Lier la méthode à l'instance de la classe dans le constructeur
        this.voitureChange = this.voitureChange.bind(this);
        this.submitVoiture = this.submitVoiture.bind(this);
        this.resetVoiture = this.resetVoiture.bind(this);
        this.toggleToast = this.toggleToast.bind(this);
    }

    initialState = {
        marque: '',
        modele: '',
        couleur: '',
        immatricule: '',
        annee: '',
        prix: '',
        showToast: false, // État pour gérer l'affichage du toast
    };

    componentDidMount() {
        // Vérifiez si un ID de voiture est passé en tant que paramètre d'URL (pour la modification)
        const voitureId = this.props.match.params.id;

        if (voitureId) {
            // Si un ID de voiture est présent, chargez les données de la voiture à partir de l'API ou de votre source de données.
            // Vous devrez peut-être effectuer une requête pour récupérer les détails de la voiture à partir de son ID.
            // Utilisez ensuite ces données pour remplir le formulaire de modification.

            // Exemple d'appel à l'API pour récupérer les détails de la voiture par ID :
            axios.get(`http://localhost:8080/voitures/${voitureId}`)
                .then(response => {
                    const voitureData = response.data;
                    this.setState({
                        marque: voitureData.marque,
                        modele: voitureData.modele,
                        couleur: voitureData.couleur,
                        immatricule: voitureData.immatricule,
                        annee: voitureData.annee,
                        prix: voitureData.prix,
                    });
                })
                .catch(error => {
                    console.error("Erreur lors de la récupération des données de la voiture :", error);
                });
        }
    }

    resetVoiture() {
        this.setState(this.initialState);
    }

    submitVoiture(event) {
        event.preventDefault();
        const voiture = {
            marque: this.state.marque,
            modele: this.state.modele,
            couleur: this.state.couleur,
            immatricule: this.state.immatricule,
            annee: this.state.annee,
            prix: this.state.prix,
        };

        // Utilisez une route différente pour l'ajout et la modification
        if (this.props.match.params.id) {
            // Si un ID de voiture est présent, il s'agit d'une modification
            axios.put(`http://localhost:8080/voitures/${this.props.match.params.id}`, voiture)
                .then(response => {
                    if (response.data) {
                        this.setState(this.initialState);
                        this.toggleToast(true);
                    }
                })
                .catch(error => {
                    console.error("Erreur lors de la modification de la voiture :", error);
                });
        } else {
            // Sinon, il s'agit d'un ajout
            axios.post("http://localhost:8080/voitures", voiture)
                .then(response => {
                    if (response.data) {
                        this.setState(this.initialState);
                        this.toggleToast(true);
                    }
                })
                .catch(error => {
                    console.error("Erreur lors de l'enregistrement de la voiture :", error);
                });
        }
    }

    toggleToast(show) {
        this.setState({ showToast: show });
    }

    voitureChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
        });
    }

    render() {
        return (
            <div>
                <div style={{ display: this.state.showToast ? "block" : "none" }}>
                    <MyToast show={this.state.showToast} message="Voiture enregistrée avec succès" type="success" />
                </div>
                <Card className="border border-dark bg-dark text-white">
                    <Card.Header><FontAwesomeIcon icon={faPlusSquare} /> {this.props.match.params.id ? 'Modifier Voiture' : 'Ajouter Voiture'}</Card.Header>
                    <Card.Body>
                        <Form onReset={this.resetVoiture} onSubmit={this.submitVoiture} id="VoitureFormId">
                            <Row>
                                <Col>
                                    <FormGroup controlId="formGridMarque">
                                        <Form.Label>Marque</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Marque Voiture"
                                            name="marque"
                                            value={this.state.marque}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridModele">
                                        <Form.Label>Modèle</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Modèle Voiture"
                                            name="modele"
                                            value={this.state.modele}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridCouleur">
                                        <Form.Label>Couleur</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Couleur Voiture"
                                            name="couleur"
                                            value={this.state.couleur}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridImmatricule">
                                        <Form.Label>Immatricule</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Immatricule Voiture"
                                            name="immatricule"
                                            value={this.state.immatricule}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridAnnee">
                                        <Form.Label>Année</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Année Voiture"
                                            name="annee"
                                            value={this.state.annee}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridPrix">
                                        <Form.Label>Prix</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Prix Voiture"
                                            name="prix"
                                            value={this.state.prix}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                            </Row>
                            <Button size="sm" variant="success" type="submit"><FontAwesomeIcon icon={faSave} /> {this.props.match.params.id ? 'Modifier' : 'Ajouter'}</Button>{' '}
                            <Button size="sm" variant="info" type="reset"><FontAwesomeIcon icon={faUndo} /> Reset</Button>
                        </Form>
                    </Card.Body>
                </Card>
            </div>
        );
    }
}

export default Voiture;
