import React, { Component } from 'react';
import axios from 'axios';
import { Button, Card, Form, FormGroup, Col, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlusSquare, faSave, faUndo } from "@fortawesome/free-solid-icons";
import MyToast from './myToast';

class Voiture extends Component {
    constructor(props) {
        super(props);
        this.state = {
            marque: '',
            modele: '',
            couleur: '',
            immatricule: '',
            annee: '',
            prix: '',
            showToast: false, // État pour gérer l'affichage du toast
            modeModification: false, // État pour gérer le mode "modification"
        };

        // Lier la méthode à l'instance de la classe dans le constructeur
        this.voitureChange = this.voitureChange.bind(this);
        this.submitVoiture = this.submitVoiture.bind(this);
        this.resetVoiture = this.resetVoiture.bind(this);
        this.toggleToast = this.toggleToast.bind(this);
    }

    componentDidMount() {
        if (this.props.voitureId) {
            this.setState({ modeModification: true });
        }
    }

    componentDidUpdate(prevProps) {
        if (this.props.voitureId !== prevProps.voitureId) {
            if (this.props.voitureId) {
                this.setState({ modeModification: true });
                this.loadVoitureDetails(this.props.voitureId);
            } else {
                this.setState({ modeModification: false });
                this.resetVoiture();
            }
        }
    }

    // Méthode pour charger les détails de la voiture en mode modification
    loadVoitureDetails(voitureId) {
        axios.get(`http://localhost:8080/voitures/${voitureId}`)
            .then(response => {
                const voitureDetails = response.data;
                this.setState({
                    marque: voitureDetails.marque,
                    modele: voitureDetails.modele,
                    couleur: voitureDetails.couleur,
                    immatricule: voitureDetails.immatricule,
                    annee: voitureDetails.annee,
                    prix: voitureDetails.prix,
                });
            })
            .catch(error => {
                console.error("Erreur lors du chargement des détails de la voiture :", error);
            });
    }

    resetVoiture() {
        this.setState({
            marque: '',
            modele: '',
            couleur: '',
            immatricule: '',
            annee: '',
            prix: '',
        });
    }

    // Le reste du code reste inchangé



submitVoiture(event) {
        event.preventDefault();
        const voiture = {
            marque: this.state.marque,
            modele: this.state.modele,
            couleur: this.state.couleur,
            immatricule: this.state.immatricule,
            annee: this.state.annee,
            prix: this.state.prix,
        };

        if (this.state.modeModification) {
            // Effectuez une requête PUT pour la modification
            axios.put(`http://localhost:8080/voitures/${this.props.voitureId}`, voiture)
                .then(response => {
                    if (response.data) {
                        this.toggleToast(true); // Activez le toast en cas de succès
                    }
                })
                .catch(error => {
                    console.error("Erreur lors de la modification de la voiture :", error);
                });
        } else {
            // Effectuez une requête POST pour l'ajout
            axios.post("http://localhost:8080/voitures", voiture)
                .then(response => {
                    if (response.data) {
                        this.toggleToast(true); // Activez le toast en cas de succès
                    }
                })
                .catch(error => {
                    console.error("Erreur lors de l'ajout de la voiture :", error);
                });
        }
    }

    toggleToast(show) {
        this.setState({ showToast: show });
    }

    voitureChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
        });
    }

    render() {
        return (
            <div>
                <div style={{ display: this.state.showToast ? "block" : "none" }}>
                    <MyToast show={this.state.showToast} message="Voiture enregistrée avec succès" type="success" />
                </div>
                <Card className="border border-dark bg-dark text-white">
                    <Card.Header><FontAwesomeIcon icon={faPlusSquare} /> {this.state.modeModification ? "Modifier Voiture" : "Ajouter Voiture"}</Card.Header>
                    <Card.Body>
                        <Form onReset={this.resetVoiture} onSubmit={this.submitVoiture} id="VoitureFormId">
                            <Row>
                                <Col>
                                    <FormGroup controlId="formGridMarque">
                                        <Form.Label>Marque</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Marque Voiture"
                                            name="marque"
                                            value={this.state.marque}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridModele">
                                        <Form.Label>Modèle</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Modèle Voiture"
                                            name="modele"
                                            value={this.state.modele}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridCouleur">
                                        <Form.Label>Couleur</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Couleur Voiture"
                                            name="couleur"
                                            value={this.state.couleur}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridImmatricule">
                                        <Form.Label>Immatricule</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Immatricule Voiture"
                                            name="immatricule"
                                            value={this.state.immatricule}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridAnnee">
                                        <Form.Label>Année</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Année Voiture"
                                            name="annee"
                                            value={this.state.annee}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                                <Col>
                                    <FormGroup controlId="formGridPrix">
                                        <Form.Label>Prix</Form.Label>
                                        <Form.Control
                                            required
                                            type="text"
                                            className="bg-dark text-white"
                                            placeholder="Entrez Prix Voiture"
                                            name="prix"
                                            value={this.state.prix}
                                            autoComplete="off"
                                            onChange={this.voitureChange}
                                        />
                                    </FormGroup>
                                </Col>
                            </Row>
                            <Button size="sm" variant="success" type="submit"><FontAwesomeIcon icon={faSave} /> {this.state.modeModification ? "Modifier" : "Ajouter"}</Button>{' '}
                            <Button size="sm" variant="info" type="reset"><FontAwesomeIcon icon={faUndo} /> Reset</Button>
                        </Form>
                    </Card.Body>
                </Card>
            </div>
        );
    }
}

export default Voiture;
