import React from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';

class Bienvenue extends React.Component {
    render() {
        // Styles
        const containerStyle = {
            display: 'flex', // Utilisez Flexbox
            justifyContent: 'center', // Centre horizontalement
            alignItems: 'center', // Centre verticalement
            height: '100vh', // Utilisez 100% de la hauteur de l'écran
            marginTop: '-50px' // Ajustez en fonction de la barre de navigation si nécessaire
        };

        const cardStyle = {
            width: 'auto', // Auto pour respecter le contenu
            marginTop: '20px',
            backgroundColor: '#343a40',
            color: 'white',
            padding: '20px',
            border: '1px solid #6c757d',
            borderRadius: '5px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Ajout d'une ombre pour du relief
            textAlign: 'center', // Centrer le texte à l'intérieur de la carte
        };

        const titleStyle = {
            fontSize: '2.5rem', // Taille du titre
            fontWeight: 'bold', // Poids de la police
            marginBottom: '20px'
        };

        const blockquoteStyle = {
            fontSize: '1.2rem', // Taille de la citation
            fontStyle: 'italic', // Style de la police
            margin: '20px 0' // Marge autour de la citation
        };

        const footerStyle = {
            fontSize: '1rem', // Taille du pied de page
            color: '#dee2e6' // Couleur claire pour le pied de page
        };

        return (
            <Container fluid style={containerStyle}>
                <Row>
                    <Col>
                        <Card style={cardStyle}>
                            <Card.Body>
                                <h1 style={titleStyle}>Bienvenue au Magasin des Voitures</h1>
                                <blockquote style={blockquoteStyle}>
                                    <p>Le meilleur de nos voitures est exposé près de chez vous.</p>
                                    <footer style={footerStyle}>IDSIT</footer>
                                </blockquote>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default Bienvenue;
