import React from 'react';
import { Card, Table } from 'react-bootstrap';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faList} from "@fortawesome/free-solid-svg-icons"; // Ajoutez Table dans les imports

class VoitureListe extends React.Component {
    render() {
        return (
            <Card className="border border-dark bg-dark text-white">
                <Card.Header><FontAwesomeIcon icon={faList}/>Liste Voitures</Card.Header>
                <Card.Body>
                    <Table bordered hover striped variant="dark">
                        <thead>
                        <tr>
                            <th>Marque</th>
                            <th>Modèle</th>
                            <th>Couleur</th>
                            <th>Année</th>
                            <th>Prix</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr align="center">
                            <td colSpan="5">Aucune Voiture n'est disponible</td>
                        </tr>
                        {/* Vous pouvez ajouter d'autres lignes de données ici si nécessaire */}
                        </tbody>
                    </Table>
                </Card.Body>
            </Card>
        );
    }
}

export default VoitureListe;
