import React from 'react';
import { Button, Card, Form, FormControl, FormGroup, Col } from 'react-bootstrap';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPlusSquare, faSave} from "@fortawesome/free-solid-svg-icons";

class Voiture extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            marque: '',
            modele: '',
            couleur: '',
            annee: '',
            prix: '',
        };
    }

    submitVoiture(event) {
        alert(this.state.marque);
        event.preventDefault();
    }

    voitureChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
        });
    }

    render() {
        return (
            <Card className="border border-dark bg-dark text-white">
                <Card.Header><FontAwesomeIcon icon={faPlusSquare}/>Ajouter Voiture</Card.Header>
                <Card.Body>
                    <Form onSubmit={this.submitVoiture} id="VoitureFormId">
                        <FormGroup as={Col} controlId="formGridMarque">
                            <Form.Label>Marque</Form.Label>
                            <FormControl
                                required
                                type="text"
                                className="bg-dark text-white"
                                placeholder="Entrez Marque Voiture"
                                name="marque"
                                value={this.state.marque}
                                onChange={this.voitureChange}
                            />
                        </FormGroup>
                        <FormGroup as={Col} controlId="formGridModele">
                            <Form.Label>Modèle</Form.Label>
                            <FormControl
                                required
                                type="text"
                                className="bg-dark text-white"
                                placeholder="Entrez Modèle Voiture"
                                name="modele"
                                value={this.state.modele}
                                onChange={this.voitureChange}
                            />
                        </FormGroup>
                        <FormGroup as={Col} controlId="formGridCouleur">
                            <Form.Label>Couleur</Form.Label>
                            <FormControl
                                required
                                type="text"
                                className="bg-dark text-white"
                                placeholder="Entrez Couleur Voiture"
                                name="couleur"
                                value={this.state.couleur}
                                onChange={this.voitureChange}
                            />
                        </FormGroup>
                        <FormGroup as={Col} controlId="formGridAnnee">
                            <Form.Label>Année</Form.Label>
                            <FormControl
                                required
                                type="text"
                                className="bg-dark text-white"
                                placeholder="Entrez Année Voiture"
                                name="annee"
                                value={this.state.annee}
                                onChange={this.voitureChange}
                            />
                        </FormGroup>
                        <FormGroup as={Col} controlId="formGridPrix">
                            <Form.Label>Prix</Form.Label>
                            <FormControl
                                required
                                type="text"
                                className="bg-dark text-white"
                                placeholder="Entrez Prix Voiture"
                                name="prix"
                                value={this.state.prix}
                                onChange={this.voitureChange}
                            />
                        </FormGroup>
                       
                    </Form>
                </Card.Body>
                <Card.Footer style={{"textAlign":"right"}}>
                    <Button size="sm" variant="success" type="submit" ><FontAwesomeIcon icon={faSave}/></Button>
                </Card.Footer>
            </Card>
        );
    }
}

export default Voiture;
