import React from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';

class Bienvenue extends React.Component {
    render() {
        // Styles
        const containerStyle = {
            paddingTop: '20px', // Ajoutez un peu d'espace en haut de la page
            paddingBottom: '20px', // Et un peu en bas pour éviter tout défilement si le contenu est plus grand que l'écran
        };

        const cardStyle = {
            backgroundColor: '#343a40',
            color: 'white',
            padding: '40px', // Un padding généreux pour un meilleur aspect visuel
            border: 'none',
            borderRadius: '5px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)', // Une ombre pour un effet de profondeur
            // Pas besoin de maxWidth si vous souhaitez que la carte s'étende sur toute la largeur disponible
        };

        const titleStyle = {
            fontSize: '2.5rem', // Taille de police plus grande pour le titre
            fontWeight: 'bold',
            marginBottom: '30px', // Espace sous le titre
        };

        const blockquoteStyle = {
            fontSize: '1.2rem', // Taille de police légèrement plus grande pour la citation
            fontStyle: 'italic',
            margin: '20px 0', // Espace autour de la citation
        };

        const footerStyle = {
            fontSize: '1rem', // Taille de police pour le footer
            color: '#dee2e6'
        };

        return (
            <Container fluid style={containerStyle}>
                <Row className="justify-content-md-center">
                    <Col lg={8} md={10}>
                        <Card style={cardStyle}>
                            <Card.Body>
                                <h1 style={titleStyle}>Bienvenue au Magasin des Voitures</h1>
                                <blockquote style={blockquoteStyle}>
                                    <p>Le meilleur de nos voitures est exposé près de chez vous.</p>
                                    <footer style={footerStyle}>IDSIT</footer>
                                </blockquote>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default Bienvenue;
