import React, { Component } from 'react';
import { Card, Table, Button, ButtonGroup } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faList, faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import MyToast from './myToast';
import { Link } from 'react-router-dom';

class VoitureListe extends Component {
    constructor(props) {
        super(props);
        this.state = {
            voitures: [],
            show: false, // Ajout de l'état pour afficher/cacher le Toast
            message: '', // Message à afficher dans le Toast
            voitureIdToDelete: null, // ID de la voiture à supprimer
        };
    }

    componentDidMount() {
        this.fetchVoitures();
    }

    fetchVoitures() {
        axios
            .get('http://localhost:8080/voitures')
            .then((response) => response.data)
            .then((data) => {
                this.setState({ voitures: data });
            })
            .catch((error) => {
                console.error('Erreur lors de la récupération des données :', error);
            });
    }

    handleEditClick(voitureId) {
        // Vous pouvez ajouter ici la logique pour éditer la voiture.
        // Par exemple, mettez à jour l'état pour indiquer que vous éditez une voiture.
        console.log("Édition de la voiture avec l'ID : " + voitureId);
    }

    handleDeleteClick(voitureId) {
        // Afficher le Toast en mettant à jour l'état
        this.setState({ show: true, message: 'Voiture supprimée avec succès.', voitureIdToDelete: voitureId });

        // Cacher le Toast après 3 secondes
        setTimeout(() => {
            this.setState({ show: false, message: '', voitureIdToDelete: null });
        }, 3000);

        // Vous pouvez également effectuer la suppression ici si nécessaire
        axios
            .delete(`http://localhost:8080/voitures/${voitureId}`)
            .then((response) => {
                if (response.data != null) {
                    // Voiture supprimée avec succès
                    // Vous pouvez également mettre à jour l'état pour refléter les changements
                    this.fetchVoitures(); // Rechargez la liste des voitures après la suppression
                }
            })
            .catch((error) => {
                console.error('Erreur lors de la suppression de la voiture :', error);
            });
    }

    render() {
        return (
            <div className="voiture-liste">
                <div style={{ display: this.state.show ? 'block' : 'none' }}>
                    <MyToast show={this.state.show} message="Voiture supprimée avec succès" type="danger" />
                </div>

                <Card className="border border-dark table-card">
                    <Card.Header className="table-header">
                        <FontAwesomeIcon icon={faList} /> Liste Voitures
                    </Card.Header>
                    <Card.Body className="table-container">
                        <Table bordered hover striped variant="dark" className="table">
                            <thead>
                            <tr>
                                <th>Marque</th>
                                <th>Modèle</th>
                                <th>Couleur</th>
                                <th>Immatricule</th>
                                <th>Année</th>
                                <th>Prix</th>
                                <th>Actions</th>
                            </tr>
                            </thead>
                            <tbody>
                            {this.state.voitures.length ? (
                                this.state.voitures.map((voiture) => (
                                    <tr key={voiture.id}>
                                        <td>{voiture.marque}</td>
                                        <td>{voiture.modele}</td>
                                        <td>{voiture.couleur}</td>
                                        <td>{voiture.immatricule}</td>
                                        <td>{voiture.annee}</td>
                                        <td>{voiture.prix}</td>
                                        <td>
                                            <ButtonGroup>
                                                <Link to={'/edit/' + voiture.id} className="btn btn-sm btn-outline-primary" style={{ marginRight: '8px' }}>
                                                    <FontAwesomeIcon icon={faEdit} /> Modifier
                                                </Link>
                                                <Button
                                                    variant="outline-danger"
                                                    onClick={() => this.handleDeleteClick(voiture.id)}
                                                >
                                                    <FontAwesomeIcon icon={faTrash} /> Supprimer
                                                </Button>
                                            </ButtonGroup>
                                        </td>
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="7" align="center">
                                        Aucune Voiture n'est disponible.
                                    </td>
                                </tr>
                            )}
                            </tbody>
                        </Table>
                    </Card.Body>
                    <Card.Footer className="card-footer">
                        <Table bordered hover striped variant="dark">
                            <tbody>
                            <tr>
                                <td colSpan="7" align="center">
                                    {this.state.voitures.length} Voitures disponibles
                                </td>
                            </tr>
                            </tbody>
                        </Table>
                    </Card.Footer>
                </Card>
            </div>
        );
    }
}

export default VoitureListe;
