import React from 'react';
import { Card, Table } from 'react-bootstrap';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faList} from "@fortawesome/free-solid-svg-icons";
import axios from "axios"; // Ajoutez Table dans les imports

class VoitureListe extends React.Component {
    lenght;
    voiture;
    constructor(props) {
        super(props);
        this.state = {
            voiture: [],
        };
    }

    componentDidMount() {
        axios.get("http://localhost:8080/voitures")
            .then(response => {
                this.setState({
                    voitures: response.data
                });
            })
            .catch(error => {
                console.error(error);
            });
    }
    render() {
        return (
            <Card className="border border-dark bg-dark text-white">
                <Card.Header><FontAwesomeIcon icon={faList}/>Liste Voitures</Card.Header>
                <Card.Body>
                    <Table bordered hover striped variant="dark">
                        <thead>
                        <tr>
                            <th>Marque</th>
                            <th>Modèle</th>
                            <th>Couleur</th>
                            <th>Année</th>
                            <th>Prix</th>
                        </tr>
                        </thead>
                        <tbody>
                        <tr align="center">
                            <td colSpan="5">
                                {this.state.voiture.lenght} Voitures  disponibles</td>
                        </tr>
                        {/* Vous pouvez ajouter d'autres lignes de données ici si nécessaire */}
                        </tbody>
                    </Table>
                </Card.Body>
            </Card>
        );
    }
}

export default VoitureListe;
