import React from 'react';
import { Card, Col, Container, Row } from 'react-bootstrap';

class Bienvenue extends React.Component {
    render() {
        // Styles
        const containerStyle = {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            minHeight: '100vh', // Assurez-vous que le conteneur est au moins aussi grand que la fenêtre de visualisation
            padding: '50px' // Ajoutez du padding pour ne pas toucher les bords de l'écran
        };

        const cardStyle = {
            maxWidth: '800px', // Fixez une largeur maximale pour la carte
            width: '100%', // La carte prendra le maximum possible jusqu'à 800px
            backgroundColor: '#343a40',
            color: 'white',
            padding: '40px', // Augmentez le padding pour plus d'espace autour du contenu
            border: 'none',
            borderRadius: '5px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)', // Rendez l'ombre plus prononcée pour un effet de profondeur
            textAlign: 'center',
        };

        const titleStyle = {
            fontSize: '3rem', // Augmentez la taille de la police pour le titre
            fontWeight: 'bold',
            marginBottom: '30px', // Espace sous le titre
        };

        const blockquoteStyle = {
            fontSize: '1.5rem', // Augmentez la taille de la citation
            fontStyle: 'italic',
            margin: '20px 0', // Espace autour de la citation
        };

        const footerStyle = {
            fontSize: '1.1rem', // Ajustez la taille de la police pour le pied de page
            color: '#dee2e6'
        };

        return (
            <Container fluid style={containerStyle}>
                <Row>
                    <Col>
                        <Card style={cardStyle}>
                            <Card.Body>
                                <h1 style={titleStyle}>Bienvenue au Magasin des Voitures</h1>
                                <blockquote style={blockquoteStyle}>
                                    <p>Le meilleur de nos voitures est exposé près de chez vous.</p>
                                    <footer style={footerStyle}>IDSIT</footer>
                                </blockquote>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default Bienvenue;
